import ExpenseItem from "./ExpenseItem"
import Card from "../UI/Card"
import '../Expenses/Expenses.css'
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter"
import { useState } from "react"
const Expenses = (props) => {
    let filterResult
    const [year, setYear] = useState('')
    const onSelectedYear = (selectedYear) => {
        setYear(selectedYear)
    }

    const getExpenses = (exp) => {
        console.log(exp)
        if(year !== ''){
            if(exp.date.getFullYear() == year)
            return exp
        }
       
    }
    return(
        <div>
            <ExpensesFilter onSelectedYear={onSelectedYear}/>
                {
                    <>
                        {
                            console.log("count " + props.expenses.filter(getExpenses).count())
                            
                        }
                        {
                            props.expenses.filter(getExpenses).map((exp) => {
                                return <Card className="expenses"><ExpenseItem key={exp.id}
                                    title={exp.title}
                                    price={exp.amount}
                                    date={exp.date}
                                ></ExpenseItem>  </Card>
                            })
                        }
                    </>
                    
                    
                    // props.expenses.map((exp)=> {
                    // return <ExpenseItem key={exp.id}
                    //     title={exp.title}
                    //     price={exp.amount}
                    //     date={exp.date}
                    // ></ExpenseItem>        
                    
                    // })
                }
            {/* </Card> */}
        </div>
        
    )
}
export default Expenses
