import React, { useState } from "react";
import '../ExpensesFilter/Filterbar.css'

const Filterbar = (props) => {
    const moment = require('moment');
    const mnths = moment.monthsShort()

    const getMonth = (exp) => {
        let mnt = exp.date.toLocaleString("en-US", { month: 'long' })
        return mnt
    }
    return (
        mnths.map(mnt => {
            return <div key={mnt}>
                {
                    props.getExpenses.map((exp) => {
                        const month = exp.date.toLocaleString("en-US", {month: 'short' })
                        const day = exp.date.toLocaleString("en-US", {day: '2-digit' })
                        return <div key={exp.date}>
                            {                              
                                mnt === month ?
                                    <div class="progress progress-bar-vertical">
                                        <div class="progress-bar progress-striped" role="progressbar" aria-valuenow="60" aria-valuemin="0" aria-valuemax="100" style={{height: 60 + '%'}}>
                                        </div>
                                    </div>
                                    
                                :

                                    <div class="progress progress-bar-vertical">
                                        <div class="progress-bar progress-striped" role="progressbar" aria-valuenow="60" aria-valuemin="0" aria-valuemax="100" style={{height: 60 + '%'}}>
                                        </div>
                                    </div>
                                
                            }
                        </div>


                    })
                }
            </div>

        })
    )
}

export default Filterbar
