import ExpenseItem from "./ExpenseItem"
import Card from "../UI/Card"
import '../Expenses/Expenses.css'
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter"
import { useState } from "react"
import Filterbar from "../ExpensesFilter/Filterbar"

const Expenses = (props) => {
    let filterResult
    const [year, setYear] = useState('')
    const expensesMonths = [];
    const onSelectedYear = (selectedYear) => {
        setYear(selectedYear)
    }

    const getExpenses = (exp) => {
        //console.log(exp)
        if(year !== ''){
            if(exp.date.getFullYear() == year)
            return exp
        }
       
    }

    const getSelectedMonths = (exp) => {
        console.log(exp);
    }

    const getExpensesMonths = (exp) => {
        const month = exp.date.toLocaleString("en-US", { month: 'long' })
        const day = exp.date.toLocaleString("en-US", { day: '2-digit' })
            
        // console.log(exp)
        // if(year !== ''){
        //     if(exp.date.() == year)
        //     return exp
        // }
    }

    return(
        <div>
            <ExpensesFilter onSelectedYear={onSelectedYear}/>
                {
                    <>
                       {
                            props.expenses.filter(getExpenses).length !== 0 ?
                            
                            <>
                            
                            {/* <Filterbar expenses={props.expenses} getExpenses={getExpenses} /> */}
                            {
                                props.expenses.filter(getExpenses).map((exp) => {
                                    return <>
                                        
                                        <Card className="expenses"><ExpenseItem key={exp.id}
                                            title={exp.title}
                                            price={exp.amount}
                                            date={exp.date}
                                        ></ExpenseItem>  </Card>
                                    </>
                                })
                            }
                            </>
                            :
                            <Card className="expenses"><div className="card expense-item"><h1>No Results Found</h1></div></Card>                   
                            
                        }
                    </>
                    
                    
                    // props.expenses.map((exp)=> {
                    // return <ExpenseItem key={exp.id}
                    //     title={exp.title}
                    //     price={exp.amount}
                    //     date={exp.date}
                    // ></ExpenseItem>        
                    
                    // })
                }
            {/* </Card> */}
        </div>
        
    )
}
export default Expenses
