import React, { useState } from 'react';
import './App.css';
import Expenses from './components/Expenses/Expenses';
import NewExpense from './components/NewExpense/NewExpense';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from 'react-router-dom';
import ExpensesFilter from './components/ExpensesFilter/ExpensesFilter';

const Dummy_Expense = [
  {
    id: 'e1',
    title: 'Toilet Paper',
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: 'e2', title: 'New TV', amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: 'e3',
    title: 'Car Insurance',
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: 'e4',
    title: 'New Desk (Wooden)',
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

const App = () => {
  const [expenses, SetExpenses] = useState(Dummy_Expense)
  const [year, setYear] = useState('')
  const onSelectedYear = (selectedYear) => {
      setYear(selectedYear)
  }
  const addExpenseHandler = expense => {
    SetExpenses(prevExpenses => {
      return [expense, ...prevExpenses]
    })
  }
  // return React.createElement(
  //   'div',
  //   {},
  //   React.createElement('h2', {}, "Let's Get Started"),
  //   React.createElement(Expenses, {expenses:expenses})
  // )
  return (
      <Router>
           <div className="App">
           <ul>
              <li>
                <Link to="/">Expenses</Link>
              </li>
              <li>
                <Link to="../NewExpense/NewExpense">Add Expenses</Link>
              </li>
              <li>
                <Link to="/contact">Expenses Filter</Link>
              </li>
            </ul>
           </div>
           <Routes>
              <Route exact path='/' element={< Expenses expenses={expenses} />}></Route>
              <Route exact path='/NewExpense/NewExpense' element={< NewExpense onAddExpense={addExpenseHandler} />}></Route>
              <Route exact path='/ExpensesFilter/ExpensesFilter' element={< ExpensesFilter onSelectedYear={onSelectedYear}/>}></Route>
          </Routes>
       </Router>
    // <div>
    //   <NewExpense onAddExpense={addExpenseHandler}/>      
    //  <Expenses expenses={expenses}/>
    // </div>
  );
}

export default App;
