    let filterResult
    const [year, setYear] = useState('')
    const expensesMonths = [];
    const onSelectedYear = (selectedYear) => {
        setYear(selectedYear)
    }

    const getExpenses = (exp) => {
        //console.log(exp)
        if(year !== ''){
            if(exp.date.getFullYear() == year){
                getSelectedMonths(exp)
                return exp
            }
            
        }
       
    }
