        console.log(exp);
        const month = exp.date.toLocaleString("en-US", { month: 'long' })
        const day = exp.date.toLocaleString("en-US", { day: '2-digit' })
       
        if(expensesMonths && expensesMonths.length !== 0){
            expensesMonths.filter(mp => mp.month === month && mp.day === day).map(ex => {
                expensesMonths.push({
                    month: ex.month,
                    day: day
                })
            })
           //     if(expensesMonths.filter(mp => mp.month !== month && mp.day !== day))
        //     {
        //         console.log("value not exist") 
        //         // expensesMonths.push({
        //         //     month: month,
        //         //     day: day
        //         // })
        //     }
        //    else {
        //         //value not exist
        //         console.log("value exist") 
        //    }
            
        }
        else {
            expensesMonths.push({
                month: month,
                day: day
            })
        }
       
       console.log(expensesMonths)
    }

    const getExpensesMonths = (exp) => {
        const month = exp.date.toLocaleString("en-US", { month: 'long' })
        const day = exp.date.toLocaleString("en-US", { day: '2-digit' })
            
        // console.log(exp)
        // if(year !== ''){
        //     if(exp.date.() == year)
        //     return exp
        // }
    }

    return(
        <div>
            <ExpensesFilter onSelectedYear={onSelectedYear}/>
                {
                    <>
                       {
                            props.expenses.filter(getExpenses).length !== 0 ?
                            
                            <>
                            
                            {/* <Filterbar expenses={props.expenses} getExpenses={getExpenses} /> */}
                            {
                                props.expenses.filter(getExpenses).map((exp) => {
                                    return <>
                                        
                                        <Card className="expenses"><ExpenseItem key={exp.id}
                                            title={exp.title}
                                            price={exp.amount}
                                            date={exp.date}
                                        ></ExpenseItem>  </Card>
                                    </>
                                })
                            }
                            </>
                            :
                            <Card className="expenses"><div className="card expense-item"><h1>No Results Found</h1></div></Card>                   
                            
                        }
                    </>
                    
                    
                    // props.expenses.map((exp)=> {
                    // return <ExpenseItem key={exp.id}
                    //     title={exp.title}
                    //     price={exp.amount}
                    //     date={exp.date}
                    // ></ExpenseItem>        
                    
                    // })
                }
            {/* </Card> */}
        </div>
        
    )
}
export default Expenses
